mapper.select:
select u.id as id, u.pid as pid, u.user_type as userType, CONCAT(u.lastname,u.firstname) as lastname, case when u.pid is null then g.org_name else g2.org_name end as orgName, case when u.pid is null then g.prov_code else g2.prov_code end as mailProvCode, case when u.pid is null then g.city_code else g2.city_code end as mailCityCode, case when u.pid is null then g.is_manage_org else g2.is_manage_org end as isFanhua, u.login_name as loginName, u.is_mgr as isMgr, u.weichatid as weichatid, GROUP_CONCAT(DISTINCT a.prov_code) as mailAddress, GROUP_CONCAT(DISTINCT a2.report_no) as bankNo from ct_user u left join ct_group g on g.user_id = u.id left join ct_group g2 on g2.user_id = u.pid left join ct_user_auth_area a on a.auth_id = 10 and a.rule_type = 1 and a.user_id = u.id left join ct_user_auth_area a2 on a2.auth_id = 10 and a2.rule_type = 2 and a2.user_id = u.id
u.id = #{userId}
limit 1



	AND (o.buyer_user_id IN 

		    #{uId}

	or o.send_id in 

		    #{uId}

		 )



	) o1
GROUP BY state



	AND (o.buyer_user_id IN ( SELECT t.grant_id_c FROM ct_third_apply_info t WHERE t.`status` = '2' AND t.service_id = 1 AND t.grant_type = '1' AND (t.apply_id_a = #{userId} OR t.middle_id_b = #{userId}) )
		OR o.send_id = #{userId}				


		OR ( o.ext1 = #{provCode}
		AND o.ext2 NOT IN 

		    #{cCode}

		)


	    OR o.ext2 = #{cityCode}


	)



	) o1
GROUP BY state



	AND o.seller_user_id IN

		    #{uId}



	) o1
GROUP BY state

select o.deal_stat as state, count(1) as sumCount
	FROM hy_order o

		(o.buyer_user_id IN 

		    #{uId}

		or o.send_user_id IN 

		    #{uId}

		 )


GROUP BY o.deal_stat

select o.deal_stat as state, count(1) as sumCount
	FROM hy_order o

		AND (o.buyer_user_id IN ( SELECT t.grant_id_c FROM ct_third_apply_info t WHERE t.`status` = '2' AND t.service_id = 1 AND t.grant_type = '1' AND (t.apply_id_a = #{userId} OR t.middle_id_b = #{userId}) )
			OR o.send_user_id = #{userId})



			AND o.prov_code = #{provCode}
			AND o.city_code NOT IN 

			    #{cCode}



		    AND o.city_code = #{cityCode}



GROUP BY o.deal_stat

AND o.import_type = '0' AND o.service_id = 1
AND ((o.subject_id = '0' AND TIMESTAMPDIFF(HOUR, o.send_time, now()) > 11) OR (o.subject_id in ('1','2','3') AND TIMESTAMPDIFF(HOUR, o.send_time, now()) > 23)) AND o.deal_stat = '04'
AND ((o.subject_id = '0' AND TIMESTAMPDIFF(HOUR, o.send_time, now()) <= 11) OR (o.subject_id in ('1','2','3') AND TIMESTAMPDIFF(HOUR, o.send_time, now()) <= 23)) AND o.deal_stat = '04'
AND o.deal_stat = #{dealStat}
AND o.is_remote = '0' AND ( o.buyer_user_id IN ( SELECT t.grant_id_c FROM ct_third_apply_info t WHERE t.`status` = '2' AND t.service_id = 1 AND t.grant_type = '1' AND (t.apply_id_a = #{userId} OR t.middle_id_b = #{userId}) ) OR o.send_id = #{userId} )
AND o.is_remote = '1'
AND o.ext1 = #{provCode} AND o.ext2 NOT IN
#{cCode}
AND o.ext2 = #{cityCode}
AND o.is_remote = '1' AND (o.buyer_user_id IN (SELECT t.grant_id_c FROM ct_third_apply_info t WHERE t.`status` = '2' AND t.service_id = 1 AND t.grant_type = '1' AND (t.apply_id_a = #{userId} OR t.middle_id_b = #{userId}) ) OR o.send_id = #{userId} )
AND o.is_remote = '9'
AND ( (o.buyer_user_id IN (SELECT t.grant_id_c FROM ct_third_apply_info t WHERE t.`status` = '2' AND t.service_id = 1 AND t.grant_type = '1' AND (t.apply_id_a = #{userId} OR t.middle_id_b = #{userId}) ) OR o.send_id = #{userId} ) OR ( o.is_remote = '1'
AND o.ext1 = #{provCode} AND o.ext2 NOT IN
#{cCode}
AND o.ext2 = #{cityCode}
) )
ORDER BY o.send_time DESC



AND o.service_id = 5

AND o.deal_stat = #{dealStat}

AND (o.buyer_user_id IN 

    #{uId}

 OR o.send_user_id IN 

    #{uId}

)


ORDER BY o.send_time DESC



AND o.service_id = 5

AND o.deal_stat = #{dealStat}

AND o.seller_user_id IN 

    #{uId}



ORDER BY o.send_time DESC
